import { useState } from "react";

function App() {
  return (
    <div className="app">
      <ToDOList />
    </div>
  );
}

function ToDOList() {
  const [tasks, setTask] = useState(["Go to Church", "Walk a dog"]);
  const [newTask, setNewTask] = useState("");

  function addTask() {
    if (!newTask) return;
    setTask((tasks) => [...tasks, newTask]);
    setNewTask("");
  }
  function deleteTask(index) {
    const updatedTasks = tasks.filter((task, i) => i !== index);
  }
  function editTask(index) {}
  function moveTaskUp(index) {}
  function moveTaskDown(index) {}

  return (
    <div className="to-do-list">
      <h1 className="to-do-title">To Do List</h1>

      <div className="first-input-section">
        <input
          type="text"
          className="enter-task"
          placeholder="Enter a task..."
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <button className="add-btn" onClick={() => addTask()}>
          Add
        </button>
      </div>
      <ul className="list-of-tasks">
        {tasks.map((task, index) => (
          <li key={index}>
            <span className="each-task">{task}</span>
            <button
              className="btn delete-btn"
              onClick={() => deleteTask(index)}
            >
              Delete
            </button>
            <button className="btn edit-btn" onClick={() => editTask(index)}>
              Edit
            </button>
            <button className="btn move-btn" onClick={() => moveTaskUp(index)}>
              Move Up
            </button>
            <button
              className="btn move-btn"
              onClick={() => moveTaskDown(index)}
            >
              Move Down
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
